<template>
  <input
    type="text"
    :value="text"
    @input="$emit('change', $event.target.value)"
  >
</template>

<script>
export default {
  name: 'CustomVModel',
  model: {
    prop: 'text',
    event: 'change'
  },
  props: {
    text: {
      type: String,
      default () {
        return ''
      }
    }
  }
}
</script>
