<template>
  <div class='icon-swiper'>
    <swiper :options="swiperOption">
      <swiper-slide v-for="(page, index) of pages" :key="index">
        <div
          class="icon-contet"
          v-for="item of page"
          :key="item.id"
        >
          <img :src='item.imgUrl'/>
          <p>{{ item.desc }}</p>
        </div>
      </swiper-slide>
    </swiper>
  </div>
</template>

<script>
export default {
  name: 'IconSwiper',
  props: {
    list: Array
  },
  data () {
    return {
      swiperOption: {
        autoplay: false
      }
    }
  },
  computed: {
    pages () {
      const pages = []
      this.list.forEach((item, index) => {
        const page = Math.floor(index / 8)
        if (!pages[page]) {
          pages[page] = []
        }
        pages[page].push(item)
      })
      return pages
    }
  }
}
</script>

<style lang='scss'>
.icon-swiper {
  .swiper-slide {
    margin: .2rem 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: start;
  }

  .icon-contet {
    width: 25%;

    img {
      width: 80%;
      margin: 0 auto;
      display: block;
      box-sizing: border-box;
      padding: 5px;
    }

    p {
      text-align: center;
      padding: 5px 0;
    }
  }
}
</style>
