<!--子组件-->
<template>
  <div @click="clickCount">点击</div>
</template>

<script>
export default {
  props: ['show'],
  data: function () {
    return {
      needShow: this.show
    }
  },
  methods: {
    clickCount: function () {
      this.needShow = !this.needShow
      this.$emit('inc', this.needShow)
    }
  }
}
</script>
