import Vue from 'vue'
import Router from 'vue-router'
import Home from '@/pages/home/Home'
import List from '@/pages/list/List'
import Vuex from '@/pages/vuex/Vuex'

Vue.use(Router)

export default new Router({
  routes: [
    {
      path: '/',
      name: 'Vuex',
      component: Vuex
    },
    {
      path: '/home',
      name: 'Home',
      component: Home
    },
    {
      path: '/list',
      name: 'List',
      component: List
    }
  ]
})
